def normalize_data(screen_size, data, keep_relations=False):
	border_x = .05*screen_size[0]
	border_y = .05*screen_size[1]

	shift_to_zero_x = -min(map(lambda x: x[0], data))
	max_x = max(map(lambda x: x[0] + shift_to_zero_x, data))

	shift_to_zero_y = -min(map(lambda x: x[1], data)) 
	max_y = max(map(lambda x: x[1] + shift_to_zero_y, data))

	scaling_x = (screen_size[0]-2*border_x)/max_x
	scaling_y = (screen_size[1]-2*border_y)/max_y

	total_scaling = min([scaling_x, scaling_y])
	if keep_relations:
		return list(map(lambda x: (border_x+(total_scaling*(x[0]+shift_to_zero_x)), border_y+(total_scaling*(x[1]+shift_to_zero_y))), data)), total_scaling
	else:
		return list(map(lambda x: (border_x+(scaling_x*(x[0]+shift_to_zero_x)), border_y+(scaling_y*(x[1]+shift_to_zero_y))), data)), total_scaling